CREATE FUNCTION core.fn_ceps_proximos(cep_origem character varying, raio_km integer DEFAULT 50) RETURNS TABLE(nr_cep character varying, nm_municipio character varying, cd_uf character varying, distancia_km numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        c2.nr_cep::VARCHAR,
        m.nm_municipio::VARCHAR,
        u.cd_uf::VARCHAR,
        core.fn_distancia_km(c1.latitude, c1.longitude, c2.latitude, c2.longitude) as distancia_km
    FROM core.tb_cep c1
    JOIN core.tb_cep c2 ON c2.in_ativo = true
    JOIN core.tb_municipio m ON c2.id_municipio = m.id_municipio
    JOIN core.tb_uf u ON m.id_uf = u.id_uf
    WHERE c1.nr_cep = cep_origem
      AND c1.in_ativo = true
      AND c1.latitude IS NOT NULL 
      AND c1.longitude IS NOT NULL
      AND c2.latitude IS NOT NULL 
      AND c2.longitude IS NOT NULL
      AND core.fn_distancia_km(c1.latitude, c1.longitude, c2.latitude, c2.longitude) <= raio_km
    ORDER BY distancia_km;
END;
$$;


