-- Name: gerar_cpf_valido(integer); Type: FUNCTION; Schema: core; Owner: constraints99
--

CREATE FUNCTION core.gerar_cpf_valido(numero integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    cpf_base VARCHAR;
    digito1 INT;
    digito2 INT;
    soma INT;
    i INT;
BEGIN
    -- Gera 9 dígitos base (adiciona zeros à esquerda se necessário)
    cpf_base := LPAD(numero::VARCHAR, 9, '0');

    -- Calcula primeiro dígito verificador
    soma := 0;
    FOR i IN 1..9 LOOP
        soma := soma + CAST(SUBSTRING(cpf_base, i, 1) AS INT) * (11 - i);
    END LOOP;
    digito1 := 11 - (soma % 11);
    IF digito1 >= 10 THEN
        digito1 := 0;
    END IF;

    -- Calcula segundo dígito verificador
    cpf_base := cpf_base || digito1::VARCHAR;
    soma := 0;
    FOR i IN 1..10 LOOP
        soma := soma + CAST(SUBSTRING(cpf_base, i, 1) AS INT) * (12 - i);
    END LOOP;
    digito2 := 11 - (soma % 11);
    IF digito2 >= 10 THEN
        digito2 := 0;
    END IF;

    -- Retorna CPF formatado: 111.111.111-11
    cpf_base := cpf_base || digito2::VARCHAR;
    RETURN SUBSTRING(cpf_base, 1, 3) || '.' ||
           SUBSTRING(cpf_base, 4, 3) || '.' ||
           SUBSTRING(cpf_base, 7, 3) || '-' ||
           SUBSTRING(cpf_base, 10, 2);
END;
$$;


