-- Name: gerar_disponibilidade_horarios_json(); Type: FUNCTION; Schema: core; Owner: constraints99
--

CREATE FUNCTION core.gerar_disponibilidade_horarios_json() RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
    dias TEXT[] := ARRAY['SEGUNDA', 'TERCA', 'QUARTA', 'QUINTA', 'SEXTA', 'SABADO', 'DOMINGO'];
    horarios_possiveis TEXT[] := ARRAY['08:00', '09:00', '10:00', '11:00', '14:00', '15:00', '16:00', '17:00', '18:00', '19:00', '20:00'];
    resultado JSONB := '{}'::JSONB;
    dia TEXT;
    qtd_horarios INT;
    horarios_dia TEXT[];
    i INT;
BEGIN
    -- Para cada dia da semana
    FOREACH dia IN ARRAY dias LOOP
        -- 40% de chance de não ter disponibilidade no dia
        IF random() < 0.4 THEN
            resultado := resultado || jsonb_build_object(dia, '[]'::JSONB);
        ELSE
            -- Gerar 1 a 4 horários aleatórios
            qtd_horarios := 1 + floor(random() * 4)::INT;
            horarios_dia := ARRAY[]::TEXT[];

            FOR i IN 1..qtd_horarios LOOP
                horarios_dia := array_append(
                    horarios_dia,
                    horarios_possiveis[1 + floor(random() * array_length(horarios_possiveis, 1))::INT]
                );
            END LOOP;

            resultado := resultado || jsonb_build_object(dia, to_jsonb(horarios_dia));
        END IF;
    END LOOP;

    RETURN resultado::TEXT;
END;
$$;


