CREATE FUNCTION core.gerar_disponibilidade_horarios_random() RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
    dias TEXT[] := ARRAY['SEGUNDA', 'TERCA', 'QUARTA', 'QUINTA', 'SEXTA'];
    horarios TEXT[] := ARRAY['09:00', '10:00', '11:00', '14:00', '15:00', '16:00', '17:00', '18:00'];
    resultado TEXT := '{';
    qtd_dias INT;
    qtd_horarios INT;
    dia TEXT;
    horario_idx INT;
BEGIN
    -- 2 a 5 dias disponíveis
    qtd_dias := 2 + floor(random() * 4)::INT;

    FOR i IN 1..qtd_dias LOOP
        dia := dias[i];

        IF i > 1 THEN
            resultado := resultado || ',';
        END IF;

        resultado := resultado || '"' || dia || '":[';

        -- 1 a 3 horários por dia
        qtd_horarios := 1 + floor(random() * 3)::INT;

        FOR j IN 1..qtd_horarios LOOP
            horario_idx := 1 + floor(random() * array_length(horarios, 1))::INT;

            IF j > 1 THEN
                resultado := resultado || ',';
            END IF;

            resultado := resultado || '"' || horarios[horario_idx] || '"';
        END LOOP;

        resultado := resultado || ']';
    END LOOP;

    resultado := resultado || '}';
    RETURN resultado;
END;
$$;


