--

CREATE FUNCTION core.gerar_estado_civil_random(idade integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    estados VARCHAR[] := ARRAY['SOLTEIRO', 'CASADO', 'UNIAO_ESTAVEL', 'DIVORCIADO', 'VIUVO'];
    rand_val INT;
BEGIN
    rand_val := floor(random() * 10)::INT;

    IF idade < 25 THEN
        -- Jovens: maioria solteiros
        IF rand_val IN (0, 1, 2, 3, 4, 5, 6, 7) THEN
            RETURN 'SOLTEIRO';
        ELSIF rand_val IN (8, 9) THEN
            RETURN 'UNIAO_ESTAVEL';
        ELSE
            RETURN 'SOLTEIRO';
        END IF;
    ELSIF idade < 35 THEN
        -- Adultos jovens: distribuição equilibrada
        IF rand_val IN (0, 1, 2, 3) THEN
            RETURN 'SOLTEIRO';
        ELSIF rand_val IN (4, 5, 6) THEN
            RETURN 'CASADO';
        ELSIF rand_val IN (7, 8) THEN
            RETURN 'UNIAO_ESTAVEL';
        ELSE
            RETURN 'DIVORCIADO';
        END IF;
    ELSIF idade < 50 THEN
        -- Adultos: maioria casados
        IF rand_val IN (0, 1) THEN
            RETURN 'SOLTEIRO';
        ELSIF rand_val IN (2, 3, 4, 5, 6) THEN
            RETURN 'CASADO';
        ELSIF rand_val IN (7, 8) THEN
            RETURN 'DIVORCIADO';
        ELSE
            RETURN 'UNIAO_ESTAVEL';
        END IF;
    ELSE
        -- Mais velhos: incluir viúvos
        IF rand_val = 0 THEN
            RETURN 'SOLTEIRO';
        ELSIF rand_val IN (1, 2, 3, 4, 5) THEN
            RETURN 'CASADO';
        ELSIF rand_val IN (6, 7, 8) THEN
            RETURN 'DIVORCIADO';
        ELSE
            RETURN 'VIUVO';
        END IF;
    END IF;
END;
$$;


