        ELSIF rand_val = 7 THEN
            RETURN 'PARENTES';
        ELSIF rand_val = 8 THEN
            RETURN 'AMIGOS';
        ELSE
            RETURN 'OUTROS';
        END IF;
    END IF;
END;
$$;


