--

CREATE FUNCTION core.gerar_nivel_experiencia_random() RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    rand_val FLOAT;
    nivel INT;
BEGIN
    -- Gera distribuição bell curve usando soma de valores aleatórios
    -- Distribuição aproximada: 10% nível 1, 20% nível 2, 40% nível 3, 20% nível 4, 10% nível 5
    rand_val := random() + random(); -- Soma de 2 randoms = distribuição triangular

    nivel := CASE
        WHEN rand_val < 0.3 THEN 1    -- ~10%
        WHEN rand_val < 0.7 THEN 2    -- ~20%
        WHEN rand_val < 1.3 THEN 3    -- ~40%
        WHEN rand_val < 1.7 THEN 4    -- ~20%
        ELSE 5                         -- ~10%
    END;

    RETURN nivel;
END;
$$;


