CREATE FUNCTION core.gerar_orientacao_sexual_random() RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    orientacoes VARCHAR[] := ARRAY[
        'heterossexual',
        'homossexual',
        'bissexual',
        'pansexual',
        'assexual',
        'outro',
        'prefiro_nao_informar'
    ];
    -- Distribuição realista
    distribuicao INT[] := ARRAY[70, 10, 8, 5, 2, 3, 2];
    rand_val INT;
    cumulative INT := 0;
    i INT;
BEGIN
    rand_val := floor(random() * 100)::INT;

    FOR i IN 1..array_length(distribuicao, 1) LOOP
        cumulative := cumulative + distribuicao[i];
        IF rand_val < cumulative THEN
            RETURN orientacoes[i];
        END IF;
    END LOOP;

    RETURN orientacoes[1];
END;
$$;


