--
-- Name: gerar_pacientes_diversos(integer, character varying); Type: FUNCTION; Schema: core; Owner: constraints99
--

CREATE FUNCTION core.gerar_pacientes_diversos(quantidade integer DEFAULT 50, modalidade_filtro character varying DEFAULT NULL::character varying) RETURNS TABLE(usuarios_criados integer, pacientes_criados integer, primeiro_email character varying, ultimo_email character varying, modalidade_gerada character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE
    i INT;
    usuario_id BIGINT;
    email_teste VARCHAR;
    cpf_gerado VARCHAR;
    contador_usuarios INT := 0;
    contador_pacientes INT := 0;
    primeiro_email_var VARCHAR;
    ultimo_email_var VARCHAR;

    -- Variáveis para dados randomizados BÁSICOS
    idade_random INT;
    sexo_random VARCHAR;
    problema_principal_random TEXT;
    problemas_sec_random TEXT[];
    localizacao_random TEXT;
    descricao_random TEXT;
    modalidade_random VARCHAR;
    preco_min NUMERIC;
    preco_max NUMERIC;
    dias_disponiveis_random TEXT[];

    -- Variáveis para dados randomizados INTERMEDIÁRIOS
    faixa_salarial_random VARCHAR;
    horarios_pref_random TEXT[];
    orientacao_sexual_random VARCHAR;
    identidade_genero_random VARCHAR;
    motivo_interrupcao_random VARCHAR;
    abordagem_anterior_random TEXT;
    metas_curto_random TEXT;
    metas_longo_random TEXT;
    historico_terapia_bool BOOLEAN;
    quando_parou_random DATE;
    lgbtq_importante_bool BOOLEAN;
    questoes_religiosas_bool BOOLEAN;

    -- Variáveis para NOVOS campos
    profissao_random VARCHAR;
    estado_civil_random VARCHAR;
    tempo_relacionamento_random INT;
    mora_com_random VARCHAR;
    escolaridade_random VARCHAR;
    religiao_random VARCHAR;
    religiao_outras_random TEXT;
    numero_endereco_random VARCHAR;
    complemento_endereco_random VARCHAR;
    complemento_localizacao_random TEXT;
    possui_apoio_financeiro_bool BOOLEAN;
    apresentacao_individual_random TEXT;
    disponibilidade_horarios_json TEXT;
    preferencias_profissional_random TEXT[];
BEGIN
    RAISE NOTICE '========================================';
    RAISE NOTICE 'Iniciando geração de % pacientes DIVERSIFICADOS v2.0', quantidade;
    IF modalidade_filtro IS NOT NULL THEN
        RAISE NOTICE 'Modalidade filtrada: %', modalidade_filtro;
    ELSE
        RAISE NOTICE 'Modalidades: distribuição equilibrada (33%% cada)';
    END IF;
    RAISE NOTICE '========================================';
    RAISE NOTICE '';

    FOR i IN 1..quantidade LOOP
        -- Preparar dados básicos
        email_teste := 'paciente' || i || '@gmail.com';
        cpf_gerado := core.gerar_cpf_valido(1000 + i);

        -- Verificar se email já existe
        IF EXISTS (SELECT 1 FROM core.usuarios WHERE email = email_teste) THEN
            RAISE NOTICE 'Pulando %: e-mail já existe', email_teste;
            CONTINUE;
        END IF;

        -- ===== GERAR DADOS RANDOMIZADOS BÁSICOS =====
        idade_random := 20 + (i % 50);  -- Idade entre 20 e 70 anos
        sexo_random := CASE WHEN i % 2 = 0 THEN 'Feminino' ELSE 'Masculino' END;
        problema_principal_random := core.gerar_problema_principal_random();
        problemas_sec_random := core.gerar_problemas_secundarios_random();
        localizacao_random := core.gerar_localizacao_random();
        descricao_random := core.gerar_descricao_problema_random(problema_principal_random);
        dias_disponiveis_random := core.gerar_dias_disponiveis_random();

        -- Gerar faixa de preço
        SELECT * INTO preco_min, preco_max FROM core.gerar_faixa_preco_random();

        -- ===== GERAR DADOS INTERMEDIÁRIOS =====
        faixa_salarial_random := core.gerar_faixa_salarial_random();
        horarios_pref_random := core.gerar_horarios_preferenciais_random();
        orientacao_sexual_random := core.gerar_orientacao_sexual_random();
        identidade_genero_random := core.gerar_identidade_genero_random();
        metas_curto_random := core.gerar_metas_curto_prazo_random(problema_principal_random);
        metas_longo_random := core.gerar_metas_longo_prazo_random(problema_principal_random);

        -- Flags importantes
        lgbtq_importante_bool := (i % 4 = 0);
        questoes_religiosas_bool := (i % 5 < 2);

        -- Histórico de terapia (30% tiveram terapia antes)
        historico_terapia_bool := (i % 3 = 0);
        IF historico_terapia_bool THEN
            motivo_interrupcao_random := core.gerar_motivo_interrupcao_random();
            abordagem_anterior_random := core.gerar_abordagem_anterior_random();
            quando_parou_random := CURRENT_DATE - (365 + floor(random() * 1460))::INT;
        ELSE
            motivo_interrupcao_random := NULL;
            abordagem_anterior_random := NULL;
            quando_parou_random := NULL;
        END IF;

        -- ===== GERAR NOVOS CAMPOS =====
        profissao_random := core.gerar_profissao_random(faixa_salarial_random);
        estado_civil_random := core.gerar_estado_civil_random(idade_random);
        escolaridade_random := core.gerar_escolaridade_random(faixa_salarial_random);
        mora_com_random := core.gerar_mora_com_random(estado_civil_random, idade_random);
        religiao_random := core.gerar_religiao_random();

        -- Tempo de relacionamento (baseado no estado civil)
        IF estado_civil_random IN ('CASADO', 'UNIAO_ESTAVEL', 'DIVORCIADO') THEN
            tempo_relacionamento_random := 1 + floor(random() * (idade_random - 20))::INT;
        ELSE
            tempo_relacionamento_random := NULL;
        END IF;

        -- Religião outras
        IF religiao_random = 'OUTRAS' THEN
            religiao_outras_random := core.gerar_religiao_outras_random();
        ELSE
            religiao_outras_random := NULL;
        END IF;

        -- Dados de endereço
        numero_endereco_random := (1 + floor(random() * 9999)::INT)::VARCHAR;
        complemento_endereco_random := CASE floor(random() * 5)::INT
            WHEN 0 THEN 'Apto ' || (100 + floor(random() * 900)::INT)::VARCHAR
            WHEN 1 THEN 'Casa'
            WHEN 2 THEN 'Bloco ' || chr(65 + floor(random() * 10)::INT) || ' Apto ' || (floor(random() * 100)::INT)::VARCHAR
            ELSE NULL
        END;
        complemento_localizacao_random := CASE floor(random() * 5)::INT
            WHEN 0 THEN 'Próximo ao metrô'
            WHEN 1 THEN 'Perto do shopping'
            WHEN 2 THEN 'Centro da cidade'
            ELSE NULL
        END;

        -- Apoio financeiro (baseado na faixa salarial)
        possui_apoio_financeiro_bool := CASE faixa_salarial_random
            WHEN 'ate_2000' THEN (random() < 0.7)  -- 70% têm apoio
            WHEN '2000_5000' THEN (random() < 0.4)  -- 40% têm apoio
            ELSE (random() < 0.2)  -- 20% têm apoio
        END;

        -- Apresentação individual
        apresentacao_individual_random := core.gerar_apresentacao_individual_random(
            problema_principal_random,
            idade_random,
            profissao_random,
            estado_civil_random
        );

        -- Disponibilidade de horários JSON
        disponibilidade_horarios_json := core.gerar_disponibilidade_horarios_json();

        -- Preferências profissionais
        preferencias_profissional_random := core.gerar_preferencias_profissional_random(
            problema_principal_random,
            lgbtq_importante_bool,
            questoes_religiosas_bool
        );

        -- Definir modalidade
        IF modalidade_filtro IS NOT NULL THEN
            modalidade_random := modalidade_filtro;
        ELSE
            modalidade_random := CASE
                WHEN i % 3 = 0 THEN 'online'
                WHEN i % 3 = 1 THEN 'presencial'
                ELSE 'hibrido'
            END;
        END IF;

        -- ===== INSERIR USUÁRIO =====
        INSERT INTO core.usuarios (
            nome,
            email,
            senha_hash,
            tipo,
            telefone,
            ativo,
            status_perfil,
            data_criacao,
            data_atualizacao
        ) VALUES (
            'Paciente Teste ' || i,
            email_teste,
            core.obter_senha_teste_hash(),
            'PACIENTE',
            '(11) 8' || LPAD(i::VARCHAR, 8, '0'),
            true,
            'COMPLETO',
            NOW(),
            NOW()
        ) RETURNING id INTO usuario_id;

        contador_usuarios := contador_usuarios + 1;

        -- ===== INSERIR PACIENTE COM TODOS OS CAMPOS =====
        INSERT INTO core.pacientes (
            usuario_id,
            idade,
            sexo,
            profissao,
            faixa_salarial,
            localizacao,
            problema_principal,
            problemas_secundarios,
            descricao_problema,
            nivel_urgencia,
            modalidade_preferida,
            faixa_preco_minimo,
            faixa_preco_maximo,
            dias_disponiveis,
            horarios_preferenciais,
            genero_terapeuta_preferido,
            faixa_etaria_terapeuta_preferida,
            possui_convenio,
            nome_convenio,
            disposto_particular,
            historico_terapia,
            quando_parou_terapia,
            motivo_interrupcao,
            abordagem_anterior,
            experiencia_avaliacao,
            acompanhamento_psiquiatrico,
            lgbtq_support_importante,
            orientacao_sexual,
            identidade_genero,
            questoes_religiosas_importantes,
            frequencia_sessoes,
            duracao_tratamento_esperada,
            flexibilidade_horarios,
            disponibilidade_tarefas_casa,
            interesse_grupos_terapia,
            metas_curto_prazo,
            metas_longo_prazo,
            experiencia_autoconhecimento,
            estado_civil,
            tempo_relacionamento_anos,
            escolaridade,
            mora_com,
            religiao,
            religiao_outras,
            numero_endereco,
            complemento_endereco,
            complemento_localizacao,
            possui_apoio_financeiro,
            apresentacao_individual,
            disponibilidade_horarios,
            preferencias_profissional,
            data_criacao,
            data_atualizacao
        ) VALUES (
            usuario_id,
            idade_random,
            sexo_random,
            profissao_random,
            faixa_salarial_random,
            localizacao_random,
            problema_principal_random,
            problemas_sec_random,
            descricao_random,
            1 + (i % 10),  -- Nível de urgência 1-10
            modalidade_random,
            preco_min,
            preco_max,
            dias_disponiveis_random,
            horarios_pref_random,
            -- Preferências de terapeuta
            CASE
                WHEN i % 4 = 0 THEN 'feminino'
                WHEN i % 4 = 1 THEN 'masculino'
                WHEN i % 4 = 2 THEN 'indiferente'
                ELSE 'nao_binario'
            END,
            CASE
                WHEN i % 3 = 0 THEN 'jovem'
                WHEN i % 3 = 1 THEN 'meia_idade'
                ELSE 'indiferente'
            END,
            -- Informações financeiras
            CASE WHEN i % 5 = 0 THEN true ELSE false END,
            CASE WHEN i % 5 = 0 THEN 'Unimed' ELSE NULL END,
            true,
            -- Histórico terapêutico
            historico_terapia_bool,
            quando_parou_random,
            motivo_interrupcao_random,
            abordagem_anterior_random,
            CASE WHEN historico_terapia_bool THEN 1 + (i % 5) ELSE NULL END,
            CASE WHEN i % 10 < 3 THEN true ELSE false END,
            -- Diversidade e inclusão
            lgbtq_importante_bool,
            orientacao_sexual_random,
            identidade_genero_random,
            questoes_religiosas_bool,
            -- Frequência e duração
            CASE
                WHEN i % 3 = 0 THEN 'semanal'
                WHEN i % 3 = 1 THEN 'quinzenal'
                ELSE 'mensal'
            END,
            CASE
                WHEN i % 3 = 0 THEN 'curto_prazo'
                WHEN i % 3 = 1 THEN 'medio_prazo'
                ELSE 'longo_prazo'
            END,
            -- Flexibilidade
            CASE WHEN i % 2 = 0 THEN true ELSE false END,
            CASE WHEN i % 3 = 0 THEN true ELSE false END,
            CASE WHEN i % 4 = 0 THEN true ELSE false END,
            -- Objetivos terapêuticos
            metas_curto_random,
            metas_longo_random,
            1 + (i % 10),
            -- NOVOS CAMPOS
            estado_civil_random,
            tempo_relacionamento_random,
            escolaridade_random,
            mora_com_random,
            religiao_random,
            religiao_outras_random,
            numero_endereco_random,
            complemento_endereco_random,
            complemento_localizacao_random,
            possui_apoio_financeiro_bool,
            apresentacao_individual_random,
            disponibilidade_horarios_json,
            preferencias_profissional_random,
            NOW(),
            NOW()
        );

        contador_pacientes := contador_pacientes + 1;

        -- Armazenar primeiro e último email
        IF i = 1 THEN
            primeiro_email_var := email_teste;
        END IF;
        ultimo_email_var := email_teste;

        -- Feedback a cada 10 registros
        IF i % 10 = 0 THEN
            RAISE NOTICE 'Progresso: % de % pacientes criados', i, quantidade;
        END IF;
    END LOOP;

    RAISE NOTICE '';
    RAISE NOTICE '========================================';
    RAISE NOTICE 'Geração concluída com sucesso!';
    RAISE NOTICE 'Total: % usuários, % pacientes', contador_usuarios, contador_pacientes;
    RAISE NOTICE '========================================';

    -- Retornar estatísticas
    RETURN QUERY SELECT
        contador_usuarios,
        contador_pacientes,
        primeiro_email_var,
        ultimo_email_var,
        COALESCE(modalidade_filtro, 'todas')::VARCHAR;
END;
$$;


