--

CREATE FUNCTION core.gerar_preferencias_profissional_random(problema_principal text, lgbtq_importante boolean, questoes_religiosas boolean) RETURNS text[]
    LANGUAGE plpgsql
    AS $$
DECLARE
    preferencias TEXT[];
    opcoes_gerais TEXT[] := ARRAY[
        'Experiência com adultos',
        'Abordagem acolhedora',
        'Flexibilidade de horários',
        'Disponibilidade para sessões online',
        'Experiência em atendimento de emergências'
    ];
BEGIN
    preferencias := ARRAY[]::TEXT[];

    -- Preferência baseada no problema principal
    CASE problema_principal
        WHEN 'ansiedade_estresse' THEN
            preferencias := array_append(preferencias, 'Experiência com transtornos de ansiedade');
        WHEN 'depressao_tristeza' THEN
            preferencias := array_append(preferencias, 'Experiência com transtornos de humor');
        WHEN 'relacionamento' THEN
            preferencias := array_append(preferencias, 'Experiência em terapia de casal');
        WHEN 'trauma_tept' THEN
            preferencias := array_append(preferencias, 'Especialização em trauma e TEPT');
        WHEN 'burnout' THEN
            preferencias := array_append(preferencias, 'Experiência com burnout e estresse ocupacional');
        ELSE
            preferencias := array_append(preferencias, 'Experiência clínica diversificada');
    END CASE;

    -- Se LGBTQ+ importante
    IF lgbtq_importante THEN
        preferencias := array_append(preferencias, 'Sensibilidade LGBTQIA+');
    END IF;

    -- Se questões religiosas importantes
    IF questoes_religiosas THEN
        preferencias := array_append(preferencias, 'Abertura para questões espirituais/religiosas');
    END IF;

    -- Adicionar 1-2 preferências gerais aleatórias
    FOR i IN 1..(1 + floor(random() * 2)::INT) LOOP
        preferencias := array_append(
            preferencias,
            opcoes_gerais[1 + floor(random() * array_length(opcoes_gerais, 1))::INT]
        );
    END LOOP;

    RETURN preferencias;
END;
$$;


