CREATE FUNCTION core.gerar_religiao_random() RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    religioes VARCHAR[] := ARRAY['CATOLICO', 'PROTESTANTE', 'ESPIRITA', 'KARDECISTA', 'ATEU', 'OUTRAS', 'NENHUMA'];
    -- Distribuição realista no Brasil
    distribuicao INT[] := ARRAY[50, 25, 5, 3, 7, 5, 5];
    rand_val INT;
    cumulative INT := 0;
    i INT;
BEGIN
    rand_val := floor(random() * 100)::INT;

    FOR i IN 1..array_length(distribuicao, 1) LOOP
        cumulative := cumulative + distribuicao[i];
        IF rand_val < cumulative THEN
            RETURN religioes[i];
        END IF;
    END LOOP;

    RETURN 'CATOLICO';
END;
$$;


