-- Name: gerar_sintomas_ids_random(); Type: FUNCTION; Schema: core; Owner: constraints99
--

CREATE FUNCTION core.gerar_sintomas_ids_random() RETURNS integer[]
    LANGUAGE plpgsql
    AS $$
DECLARE
    qtd INT;
    sintomas_ids INT[];
    sint_id INT;
    max_sint_id INT;
BEGIN
    -- Pegar ID máximo de sintomas
    SELECT MAX(id) INTO max_sint_id FROM core.tb_sintomas;

    -- 1 a 3 sintomas
    qtd := 1 + floor(random() * 3)::INT;

    FOR i IN 1..qtd LOOP
        LOOP
            sint_id := 1 + floor(random() * max_sint_id)::INT;
            -- Verificar se o ID existe e não está repetido
            IF EXISTS (SELECT 1 FROM core.tb_sintomas WHERE id = sint_id)
               AND (sintomas_ids IS NULL OR NOT (sint_id = ANY(sintomas_ids))) THEN
                EXIT;
            END IF;
        END LOOP;
        sintomas_ids := array_append(sintomas_ids, sint_id);
    END LOOP;

    RETURN sintomas_ids;
END;
$$;


