CREATE FUNCTION core.gerar_terapeutas_diversos(quantidade integer DEFAULT 50, modalidade_filtro character varying DEFAULT NULL::character varying) RETURNS TABLE(usuarios_criados integer, terapeutas_criados integer, primeiro_email character varying, ultimo_email character varying, modalidade_gerada character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE
    i INT;
    usuario_id BIGINT;
    email_teste VARCHAR;
    cpf_gerado VARCHAR;
    crp_gerado VARCHAR;
    contador_usuarios INT := 0;
    contador_terapeutas INT := 0;
    primeiro_email_var VARCHAR;
    ultimo_email_var VARCHAR;

    -- Variáveis para dados randomizados
    especialidades_random TEXT;
    valor_random NUMERIC;
    abordagem_random TEXT;
    abordagens_sec_random TEXT[];
    localizacao_random TEXT;
    bio_random TEXT;
    experiencia_random INT;
    modalidade_random VARCHAR;

    -- Novas variáveis para campos faltantes
    dia_reuniao_random VARCHAR;
    horario_reuniao_random VARCHAR;
    disponibilidade_horarios_random TEXT;
    dias_atendimento_random TEXT[];
BEGIN
    RAISE NOTICE '========================================';
    RAISE NOTICE 'Iniciando geração de % terapeutas DIVERSIFICADOS', quantidade;
    IF modalidade_filtro IS NOT NULL THEN
        RAISE NOTICE 'Modalidade filtrada: %', modalidade_filtro;
    ELSE
        RAISE NOTICE 'Modalidades: distribuição equilibrada (33%% cada)';
    END IF;
    RAISE NOTICE '========================================';
    RAISE NOTICE '';

    FOR i IN 1..quantidade LOOP
        -- Preparar dados básicos
        email_teste := 'teste' || i || '@gmail.com';
        cpf_gerado := core.gerar_cpf_valido(i);
        crp_gerado := core.gerar_crp(i);

        -- Verificar se email já existe
        IF EXISTS (SELECT 1 FROM core.usuarios WHERE email = email_teste) THEN
            RAISE NOTICE 'Pulando %: e-mail já existe', email_teste;
            CONTINUE;
        END IF;

        -- ===== GERAR DADOS RANDOMIZADOS =====
        especialidades_random := core.gerar_especialidades_random();
        valor_random := core.gerar_valor_sessao_random();
        abordagem_random := core.gerar_abordagem_random();
        abordagens_sec_random := core.gerar_abordagens_secundarias_random();
        localizacao_random := core.gerar_localizacao_random();
        experiencia_random := CASE
            WHEN i <= 20 THEN 5
            WHEN i <= 40 THEN 10
            ELSE 15
        END;
        bio_random := core.gerar_bio_random(
            especialidades_random,
            abordagem_random,
            experiencia_random
        );

        -- Gerar novos campos faltantes
        dia_reuniao_random := core.gerar_dia_reuniao_random();
        horario_reuniao_random := core.gerar_horario_reuniao_random();
        disponibilidade_horarios_random := core.gerar_disponibilidade_horarios_random();

        -- Gerar dias de atendimento baseado na disponibilidade
        dias_atendimento_random := ARRAY['SEGUNDA', 'TERCA', 'QUARTA', 'QUINTA', 'SEXTA'];

        -- Definir modalidade (minúsculas)
        IF modalidade_filtro IS NOT NULL THEN
            modalidade_random := LOWER(modalidade_filtro);
        ELSE
            modalidade_random := CASE
                WHEN i % 3 = 0 THEN 'online'
                WHEN i % 3 = 1 THEN 'presencial'
                ELSE 'hibrido'
            END;
        END IF;

        -- Inserir usuário
        INSERT INTO core.usuarios (
            nome,
            email,
            senha_hash,
            tipo,
            telefone,
            ativo,
            status_perfil,
            data_criacao,
            data_atualizacao
        ) VALUES (
            'Terapeuta Teste ' || i,
            email_teste,
            core.obter_senha_teste_hash(),
            'TERAPEUTA',
            '(11) 9' || LPAD(i::VARCHAR, 8, '0'),
            true,
            'COMPLETO',
            NOW(),
            NOW()
        ) RETURNING id INTO usuario_id;

        contador_usuarios := contador_usuarios + 1;

        -- Inserir terapeuta COM DADOS DIVERSIFICADOS
        INSERT INTO core.terapeutas (
            usuario_id,
            cpf,
            crp,
            instituicao_formacao,
            ano_formacao,
            especialidades,
            bio,
            localizacao_clinica,
            metodos_pagamento,
            valor_sessao,
            data_criacao,
            data_atualizacao,

            -- Dados demográficos
            genero,
            data_nascimento,
            idade_calculada,

            -- Experiência e formação
            experiencia_anos,
            pos_graduacao,
            certificacoes,
            abordagens_principais,
            abordagens_secundarias,
            tipo_terapia,

            -- Modalidade e atendimento
            modalidade_atendimento,
            faixa_etaria_atendimento,
            aceita_convenio,
            convenios_aceitos,

            -- Disponibilidade
            disponibilidade_horarios,
            dias_atendimento,
            horario_inicio,
            horario_fim,

            -- LGBTQ+ e inclusão
            lgbtq_friendly,
            formacao_diversidade,
            certificacoes_lgbtq,
            experiencia_casos_lgbtq,

            -- Avaliação e qualidade
            nota_media,
            total_avaliacoes,
            total_pacientes_atendidos,
            anos_plataforma,

            -- Configurações profissionais
            primeira_consulta_gratuita,
            valor_primeira_consulta,
            politica_cancelamento,
            tempo_resposta_horas,

            -- Campos pessoais
            religiao,
            redes_sociais,
            estado_civil,
            anos_relacionamento,
            possui_filhos,
            quantidade_filhos,
            filhos_deficiencia,
            justificativa_deficiencia,

            -- Informações pessoais extras
            assuntos_prediletos,
            hobby,
            inspiracao,
            filmes_marcantes,
            superacoes,
            causa_sangue,
            maior_mudanca,
            gastaria_dinheiro,
            marca_deixar,
            trabalho_antes_saude_mental,
            atenderia_por_amor,

            -- Campos profissionais extras
            o_que_nao_gosta_atender,
            cases_sucesso,
            feedback_pacientes,
            demanda_mais_comum,

            -- Reunião com equipe AXIS
            dia_reuniao,
            horario_reuniao,
            horario_reuniao_disponivel,

            -- Status de aprovação
            status_cadastro,
            data_aprovacao,
            aprovado_por,
            motivo_reprovacao
        ) VALUES (
            usuario_id,
            cpf_gerado,
            crp_gerado,
            'Universidade Federal de São Paulo - UNIFESP',
            2015,
            especialidades_random,
            bio_random,
            localizacao_random,
            'PIX,CREDITO,DEBITO,DINHEIRO',
            valor_random,
            NOW(),
            NOW(),

            -- Dados demográficos (MAIÚSCULAS)
            CASE WHEN i % 2 = 0 THEN 'FEMININO' ELSE 'MASCULINO' END,
            DATE '1985-01-01' + (i || ' years')::INTERVAL,
            EXTRACT(YEAR FROM AGE(NOW(), DATE '1985-01-01' + (i || ' years')::INTERVAL))::INT,

            -- Experiência e formação
            experiencia_random,
            ARRAY['Especialização em TCC', 'Pós-graduação em Psicologia Clínica'],
            ARRAY['CRP Ativo', 'Formação em Terapia Cognitivo-Comportamental'],
            ARRAY[abordagem_random],
            abordagens_sec_random,
            CASE
                WHEN i % 3 = 0 THEN 'INDIVIDUAL'
                WHEN i % 3 = 1 THEN 'CASAL'
                ELSE 'INDIVIDUAL'
            END,

            -- Modalidade e atendimento
            modalidade_random,
            'CRIANCAS,ADOLESCENTES,ADULTOS,IDOSOS',
            CASE WHEN i % 4 = 0 THEN true ELSE false END,
            CASE WHEN i % 4 = 0 THEN ARRAY['Unimed', 'SulAmérica'] ELSE NULL END,

            -- Disponibilidade
            disponibilidade_horarios_random,
            dias_atendimento_random,
            NULL,
            NULL,

            -- LGBTQ+ e inclusão
            CASE WHEN i % 3 = 0 THEN true ELSE false END,
            'Curso de Psicologia e Diversidade Sexual',
            CASE WHEN i % 3 = 0 THEN ARRAY['Certificado em Atendimento LGBTQIA+'] ELSE NULL END,
            CASE WHEN i % 3 = 0 THEN 20 ELSE 0 END,

            -- Avaliação e qualidade
            4.50 + (RANDOM() * 0.50),
            10 + (i * 2),
            50 + (i * 5),
            2.00 + (RANDOM() * 3.00),

            -- Configurações profissionais
            CASE WHEN i % 5 = 0 THEN true ELSE false END,
            CASE WHEN i % 5 = 0 THEN 100.00 ELSE NULL END,
            'Cancelamento com 24h de antecedência sem cobrança',
            24,

            -- Campos pessoais
            CASE
                WHEN i % 5 = 0 THEN 'CATOLICO'
                WHEN i % 5 = 1 THEN 'EVANGELICO'
                WHEN i % 5 = 2 THEN 'ESPIRITA'
                WHEN i % 5 = 3 THEN 'ATEU'
                ELSE 'NAO_INFORMADO'
            END,
            jsonb_build_object(
                'instagram', '@terapeuta_teste' || i,
                'linkedin', 'linkedin.com/in/terapeuta-teste-' || i
            ),
            CASE
                WHEN i % 4 = 0 THEN 'CASADO'
                WHEN i % 4 = 1 THEN 'SOLTEIRO'
                WHEN i % 4 = 2 THEN 'UNIAO_ESTAVEL'
                ELSE 'DIVORCIADO'
            END,
            CASE WHEN i % 3 = 0 THEN 5 ELSE NULL END,
            CASE WHEN i % 4 = 0 THEN true ELSE false END,
            CASE WHEN i % 4 = 0 THEN 2 ELSE NULL END,
            CASE WHEN i % 5 = 0 THEN 'true' ELSE 'false' END,
            CASE WHEN i % 5 = 0 THEN 'Filho tem deficiência auditiva' ELSE NULL END,

            -- Informações pessoais extras
            'Psicologia, Filosofia, Arte',
            'Leitura, Caminhadas, Yoga',
            'Carl Rogers e sua abordagem centrada na pessoa',
            'Soul (Pixar), Divertida Mente, Quem Somos Nós?',
            'Superação de ansiedade generalizada através da terapia',
            'Saúde mental e bem-estar para todos',
            'Ter meu próprio consultório e ampliar o acesso à terapia',
            'Se tivesse muito dinheiro, criaria clínicas sociais de psicologia',
            'Ajudar pessoas a se conhecerem e viverem melhor',
            'Trabalhei com educação antes de me especializar em saúde mental',
            'Atenderia por amor casos de pessoas em vulnerabilidade social',

            -- Campos profissionais extras
            'Não trabalho com transtornos psicóticos agudos',
            'Paciente com TAG que voltou a trabalhar após 6 meses de terapia',
            '"Melhor profissional que já consultei, muito empática!" - Paciente anônimo',
            split_part(especialidades_random, ',', 1) || ' e relacionamentos interpessoais',

            -- Reunião com equipe AXIS
            dia_reuniao_random,
            horario_reuniao_random,
            -- Criar timestamp combinando próxima ocorrência do dia_reuniao com o horário
            (CURRENT_DATE + ((
                CASE dia_reuniao_random
                    WHEN 'SEGUNDA' THEN 1
                    WHEN 'TERCA' THEN 2
                    WHEN 'QUARTA' THEN 3
                    WHEN 'QUINTA' THEN 4
                    WHEN 'SEXTA' THEN 5
                END - EXTRACT(ISODOW FROM CURRENT_DATE)::INT + 7) % 7)::INT)::TIMESTAMP
                + horario_reuniao_random::TIME,

            -- Status de aprovação (já aprovado para testes)
            'APROVADO',
            NOW(),
            1,  -- ID do admin (assumindo que existe um admin com ID 1)
            NULL  -- ✅ NOVO (motivo_reprovacao - NULL para aprovados)
        );

        contador_terapeutas := contador_terapeutas + 1;

        -- Armazenar primeiro e último email
        IF i = 1 THEN
            primeiro_email_var := email_teste;
        END IF;
        ultimo_email_var := email_teste;

        -- Feedback a cada 10 registros
        IF i % 10 = 0 THEN
            RAISE NOTICE 'Progresso: % de % terapeutas criados', i, quantidade;
        END IF;
    END LOOP;

    RAISE NOTICE '';
    RAISE NOTICE '========================================';
    RAISE NOTICE 'Geração concluída com sucesso!';
    RAISE NOTICE 'Total: % usuários, % terapeutas', contador_usuarios, contador_terapeutas;
    RAISE NOTICE '========================================';

    -- Retornar estatísticas
    RETURN QUERY SELECT
        contador_usuarios,
        contador_terapeutas,
        primeiro_email_var,
        ultimo_email_var,
        COALESCE(modalidade_filtro, 'todas')::VARCHAR;
END;
$$;


