CREATE FUNCTION core.gerar_valor_sessao_random() RETURNS numeric
    LANGUAGE plpgsql
    AS $_$
DECLARE
    faixa INT;
    valor NUMERIC;
BEGIN
    faixa := floor(random() * 5)::INT;

    valor := CASE faixa
        WHEN 0 THEN 80.00 + (random() * 40)::NUMERIC   -- R$80-120 (Popular)
        WHEN 1 THEN 120.00 + (random() * 30)::NUMERIC  -- R$120-150 (Médio)
        WHEN 2 THEN 150.00 + (random() * 50)::NUMERIC  -- R$150-200 (Intermediário)
        WHEN 3 THEN 200.00 + (random() * 80)::NUMERIC  -- R$200-280 (Premium)
        ELSE 280.00 + (random() * 70)::NUMERIC         -- R$280-350 (VIP)
    END;

    -- Arredondar para 2 casas decimais
    RETURN ROUND(valor, 2);
END;
$_$;


