    );

    -- Contar e deletar usuários de teste
    SELECT COUNT(*) INTO count_usuarios
    FROM core.usuarios
    WHERE email LIKE 'paciente%@gmail.com';

    DELETE FROM core.usuarios
    WHERE email LIKE 'paciente%@gmail.com';

    RAISE NOTICE '';
    RAISE NOTICE 'Limpeza concluída com sucesso!';
    RAISE NOTICE 'Removidos: % pacientes, % usuários', count_pacientes, count_usuarios;
    RAISE NOTICE '========================================';

    RETURN QUERY SELECT count_pacientes, count_usuarios;
END;
$$;


